import Classes from './ColumnChart.module.css'
import { useEffect, useState } from "react";
import { useParams } from 'react-router-dom';
import axios from "axios";
import { 
    BarChart, Bar, XAxis,
    YAxis, CartesianGrid, Tooltip,
    Legend, ResponsiveContainer 
} from "recharts";

function ColumnChart() {
    const { id } = useParams();
    const [userData, setUserData] = useState();

    useEffect(() => {
        axios.get('../../mock/userActivity.json')
            .then(function (response) {
                const user = response.data.find(user => user.userId === parseInt(id, 10));
                if (user) {
                    setUserData(user);
                } else {
                    console.log("Utilisateur non trouvé.");
                }
            })
            .catch(function (error) {
                console.log(error);
            });
    }, [id]);

    return (
        <div className={Classes.columnChart}>
            {userData ? (
                <ResponsiveContainer width="100%" height={400}>
                <BarChart
                  data={userData}
                  margin={{
                    top: 5,
                    right: 30,
                    left: 20,
                    bottom: 5,
                  }}
                >
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="name" fill="#282D30" />
                  <Bar dataKey="priceUsd" fill="#E60000" />
                </BarChart>
              </ResponsiveContainer>
            ): <></>
            }
        </div>
    );
}

export default ColumnChart;
