import Classes from './Dashboard.module.css';
import Header from '../Header/Header';
import SideNavBar from '../SideNavBar/SideNavBar';
import Title from '../Title/Title';
import ColumnChart from '../ColumnChart/ColumnChart';
import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function Dashboard() {
    const { id } = useParams();
    const [userData, setUserData] = useState();
    const [userDataActivity, setUserDataActivity] = useState();

    useEffect(() => {
        axios.get('../../mock/userData.json')
            .then(function (response) {
                const userData = response.data.find(user => user.id === parseInt(id, 10));
                if (userData) {
                    setUserData(userData);
                } else {
                    console.log("Utilisateur non trouvé.");
                }

                axios.get('../../mock/userActivity.json')
                    .then(function (activityResponse) {
                        const userActivity = activityResponse.data.find(user => user.userId === parseInt(id, 10));
                        if (userActivity) {
                            setUserDataActivity(userActivity);
                        } else {
                            console.log("Données d'activité introuvables.");
                        }
                    })
                    .catch(function (error) {
                        console.log(error);
                    });
            })
            .catch(function (error) {
                console.log(error);
            });
    }, [id]);

    return (
        <div className={Classes.dashboard}>
            <Header />
            <SideNavBar />
            <section className={Classes.dashboardContent}>
                <Title data={userData} />
                <main className={Classes.monitoring}>
                    <ColumnChart data={userDataActivity} />
                    <div className={Classes.AllMacronutrient}>

                    </div>
                </main>
            </section>
        </div>
    );
}

export default Dashboard;
