import Classes from './ColumnChart.module.css'
import { useEffect, useState } from "react";
import { useParams } from 'react-router-dom';
import axios from "axios";
import { 
    BarChart, Bar, XAxis,
    YAxis, CartesianGrid, Tooltip,
    Legend, ResponsiveContainer 
} from "recharts";

function ColumnChart() {
    const { id } = useParams();
    const [userData, setUserData] = useState();

    useEffect(() => {
        axios.get('../../mock/userActivity.json')
            .then(function (response) {
                const user = response.data.find(user => user.userId === parseInt(id, 10));
                if (user) {
                    setUserData(user);
                } else {
                    console.log("Utilisateur non trouvé.");
                }
            })
            .catch(function (error) {
                console.log(error);
            });
    }, [id]);

    let maxKilo = 0;
    let minKilo = 0;

    if (userData) {
        userData.sessions.forEach( it => {
            maxKilo = Math.max(maxKilo, it.kilogram);
            minKilo = Math.min(minKilo, it.kilogram);
        });
    }

    return (
        <div className={Classes.columnChart}>
            {userData ? (
                <ResponsiveContainer width="100%" height={320}>
                    <BarChart data={userData.sessions}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="day" />
                        <YAxis dataKey='kilogram' domain={[69, Math.ceil(maxKilo / 10)]} orientation='right'/>
                        <Tooltip />
                        <Legend />
                        <Bar dataKey="kilogram" name="Poids (kg)" fill="#282D30" barSize={12} shape={"round"} />
                        <Bar dataKey="calories" name="Calories brûlées" fill="#E60000" barSize={12} shape={"round"} />
                    </BarChart>
                </ResponsiveContainer>
            ) : null}
        </div>
    );
}

export default ColumnChart;
