import Classes from './Title.module.css';
import { useParams } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Title() {
    const { id } = useParams();
    const [userData, setUserData] = useState();

    useEffect(() => {
        axios.get('../../mock/userData.json')
            .then(function (response) {
                const user = response.data.find(user => user.id === parseInt(id, 10));
                if (user) {
                    setUserData(user);
                } else {
                    console.log("Utilisateur non trouvé.");
                }
            })
            .catch(function (error) {
                console.log(error);
            });
    }, [id]);

    return (
        <div className={Classes.title}>
            {userData ? (
                <div>
                    <h1>{userData.userInfos.firstName} {userData.userInfos.lastName}</h1>
                    <p>Âge : {userData.userInfos.age}</p>
                    <p>Score aujourd'hui : {userData.todayScore}</p>
                </div>
            ) : (
                <p>Chargement en cours...</p>
            )}
        </div>
    );
}

export default Title;
